/* eslint-disable no-unused-vars */
const yt = require("yt-converter")


yt.convertVideo({
    url: "https://www.youtube.com/watch?v=JzbGrvkqV5w",
    directoryDownload: __dirname,
    itag: 136
}, (percentage, id) => {
    console.log(`Downloaded ${percentage.total} in the process ${id}`)
    console.log(`Audio: ${percentage.audioTotal}  Video: ${percentage.videoTotal}`)
});
